import React  from 'react';

import Calendar from './components/Calendar';
import Login from './components/Login';
import Logout from './components/Logout';

export default class App extends React.Component {
	state = {
		date: null
	};
	handleDateChange = date => this.setState({date});

	render() {
		const { date } = this.state;

		const [value, setValue] = React.useState("");

    	function handleChangeNew(newValue) {
      	setValue(newValue);
    	}

		if(Login.state !=="")
		return (
			<div>
				<p>Hello,{Login.state} </p>
				{date && <p>Выбранная дата: {date.getDay()}.{date.getMonth()}.{date.getFullYear()}</p>}

				<Calendar
					onChange={this.handleDateChange}				
				/>
				<Login onChange={this.handleChangeNew}/>
				<Logout onChange={this.handleChangeNew}/>
			</div>

		);
		else if(Logout.state === false) return(
			<div><Login />
			<Logout /></div>
		)
	}
}
