import React from 'react';

import './index1.css';

export default class Calendar extends React.Component{
    static defaultProps = {
        date: new Date(),
        years:[2010,2011,2012,2013,2014,2015,2016,2017,2018,2019,2020,2021,2022,2023,2024,2025,2026],
        monthNames:['Яндварь','Февраль','Март','Апрель','Май','Июнь','Июль','Август','Сентябрь','Октябрь','Ноябрь','Декабрь'],
        weekDayNames:['Пн','Вт','Ср','Чт','Пт','Сб','Вс'],
        onChange: Function.prototype //пустая функция
    };

    state = {
        date: this.props.date,
        currentDate: new Date(),
        selectedDate: null
    };

    handlePrevMonthButtonClick = () => {};

    handleNextMonthButtonClick = () => {};

    handleSelectChange = () => {};

    handleDayClick = date => {
        console.log(date);
        this.setState({selectedDate: date});

        this.props.onChange(date);
    };


    render(){
        const {years, monthNames, weekDayNames} = this.props;

        const monthData = [
            [undefined,undefined,new Date(),new Date(),new Date(),new Date(),new Date()],
            [new Date(),new Date(),new Date(),new Date(),new Date(),new Date(),new Date()],
            [new Date(),new Date(),new Date(),new Date(),new Date(),new Date(),new Date()],
            [new Date(),new Date(),new Date(),new Date(),new Date(),new Date(),new Date()],
            [new Date(),new Date(),new Date(),new Date(),undefined,undefined,undefined]
        ];

        return(
            <div className="calendar">
                <header>
                    <button>{'<'}</button>

                    <select>
                        {monthNames.map((name,index) =>
                            <option key={name} value={index}>{name}</option>
                        )}
                    </select>

                    <select>
                        {years.map((year) => 
                            <option key={year} value={year}>{year}</option>
                        )}
                    </select>

                    <button>{'>'}</button>
                </header>

                <table>
                    <thead>
                        <tr>
                            {weekDayNames.map(name =>
                                <th key={name}>{name}</th>
                                )}
                        </tr>
                    </thead>

                    <tbody>
                        {monthData.map((week,index) => 
                            <tr key={index} className="week">
                                {week.map((date, index) => date ?
                                    <td 
                                    key={index} 
                                    className="day"
                                    onClick={() => this.handleDayClick(date)}>{date.getDate()}</td>
                                    :
                                    <td key={index} />
                                )}
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>

        );
    }
}
